/*
包含了所有的action creator（action的工厂函数）
*/ 
import {ADD_COMMENT,DEL_COMMENT} from './action-types'
// 添加评论
export const addComment=(comment)=>({type:ADD_COMMENT,data:comment})

// 删除评论
export const delComment=(index)=>({type:DEL_COMMENT,data:index})
