/*
包含N个reducer函数（根据老的state和action返回一个新的state）
*/
import {combineReducers} from 'redux'
import {ADD_COMMENT,DEL_COMMENT,RECEIVE_COMMENTS} from './action-types'

const initComments=[]
const comments=(state=initComments, action) =>{
    switch (action.type){
        case ADD_COMMENT:
           return [action.data,...state]
        case DEL_COMMENT:
            return state.filter((item,index)=>index!==action.data)
        case RECEIVE_COMMENTS:
            return state.concat(action.data)
        default:
            return state
    }
}

// 假如需要导出多个reduce
import {INCREASE,DECREASE} from './action-type'
function counter(state = 0, action) {
    switch (action.type) {
        case INCREASE:
            return state + action.data
        case DECREASE:
            return state - action.data
        default:
            return state
    }
}

export default combineReducers({
    comments,
    counter
})

// 以上combineReducers方法向外暴露的是一个对象
// {
//     counter:3,
//     comments:[]
// }
