/*
包含N个reducer函数（根据老的state和action返回一个新的state）
*/
import {ADD_COMMENT,DEL_COMMENT} from './action-types'

const initComments = [
    { userName: 'Tom', content: '很好的啊' },
    { userName: 'Jack', content: '怎么好的啊' }
]
export function comments(state=initComments, action) {
    switch (action.type){
        case ADD_COMMENT:
           return [action.data,...state]
        case DEL_COMMENT:
            return state.filter((item,index)=>index!==action.data)
        default:
            return state
    }
}
